import { marked } from 'marked'
import { UserIcon, AssistantIcon } from '../utils/icons'
import { highlightAtoms } from '../utils/highlightAtoms'
import { useAppState, useAppDispatch } from '../hooks/useAppState'

marked.setOptions({ gfm: true, breaks: true })

export function Turn({ turn, index }) {
  const { atoms, highlightedTurn } = useAppState()
  const dispatch = useAppDispatch()

  const rendered = marked.parse(turn.text)
  const highlighted = highlightAtoms(rendered, atoms)
  const Icon = turn.role === 'user' ? UserIcon : AssistantIcon
  const turnClass = turn.role === 'user' ? 'user-turn' : 'assistant-turn'
  const isHighlighted = highlightedTurn === index

  const handleAtomClick = (e) => {
    if (e.target.classList.contains('atom-highlight')) {
      e.stopPropagation()
      const term = e.target.dataset.atom
      dispatch({ type: 'SELECT_ATOM', term })
    }
  }

  return (
    <div
      className={`turn ${turnClass}${isHighlighted ? ' highlighted' : ''}`}
      data-turn={index}
    >
      <div className="turn-header">
        <div className={`turn-role ${turn.role}`}>
          <Icon />
          {turn.role}
        </div>
        <div className="turn-rule"></div>
      </div>
      <div
        className="turn-content"
        onClick={handleAtomClick}
        dangerouslySetInnerHTML={{ __html: highlighted }}
      />
    </div>
  )
}

export function UserIcon({ className = 'icon' }) {
  return (
    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2"/>
      <circle cx="12" cy="7" r="4"/>
    </svg>
  )
}

export function AssistantIcon({ className = 'icon' }) {
  return (
    <svg className={className} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M12 8V4H8"/>
      <rect width="16" height="12" x="4" y="8" rx="2"/>
      <path d="M2 14h2"/>
      <path d="M20 14h2"/>
      <path d="M15 13v2"/>
      <path d="M9 13v2"/>
    </svg>
  )
}

import { useEffect, useRef, memo } from 'react'
import { useParams, useNavigate, useLocation } from '@tanstack/react-router'
import { marked } from 'marked'
import { formatDate } from '../utils/formatDate'
import { slugify } from '../utils/slugify'
import { highlightAtoms } from '../utils/highlightAtoms'
import { UserIcon, AssistantIcon } from '../utils/icons'
import { useAppState } from '../hooks/useAppState'
import { useScrollSpy } from '../hooks/useScrollSpy'
import { scrollToElement } from '../hooks/useScrollSpy'

marked.setOptions({ gfm: true, breaks: true })

export function Reader() {
  const { conversations, atoms, activeSection, setActiveSection, scrollContainerRef } = useAppState()
  const params = useParams({ strict: false })
  const navigate = useNavigate()
  const location = useLocation()
  const isScrollingRef = useRef(false)
  const activeId = useScrollSpy(scrollContainerRef, 'section[id]', conversations.length > 0)

  const targetSlug = params.slug

  // Update URL when scroll spy detects new section (replaceState behavior)
  useEffect(() => {
    // Don't update URL while programmatic scroll is in progress
    if (isScrollingRef.current) return
    if (activeId && activeId !== activeSection) {
      setActiveSection(activeId)
      // Only update URL if we're on a conversations route
      if (location.pathname.startsWith('/conversations')) {
        navigate({
          to: '/conversations/$slug',
          params: { slug: activeId },
          replace: true // replaceState, no history
        })
      }
    }
  }, [activeId, activeSection, setActiveSection, navigate, location.pathname])

  // Scroll to target conversation on route change
  useEffect(() => {
    if (targetSlug && conversations.length > 0 && scrollContainerRef.current) {
      const el = document.getElementById(targetSlug)
      if (el) {
        isScrollingRef.current = true
        setActiveSection(targetSlug)
        scrollToElement(el, scrollContainerRef.current, 400)
        // Re-enable scroll spy after animation completes
        setTimeout(() => {
          isScrollingRef.current = false
        }, 450)
      }
    }
  }, [targetSlug, conversations.length, scrollContainerRef, setActiveSection])

  if (conversations.length === 0) {
    return <div className="empty-state">Loading...</div>
  }

  return (
    <div className="corpus">
      {conversations.map(conv => (
        <ConversationSection key={conv.filename} conversation={conv} atoms={atoms} />
      ))}
    </div>
  )
}

const ConversationSection = memo(function ConversationSection({ conversation, atoms }) {
  const slug = slugify(conversation.title)

  return (
    <section id={slug} className="conversation-section">
      <header className="reader-header">
        <h1>{conversation.title}</h1>
        <div className="reader-meta">
          {conversation.frontmatter.date && (
            <span>{formatDate(conversation.frontmatter.date)}</span>
          )}
          {conversation.frontmatter.follows && (
            <span>follows: {conversation.frontmatter.follows}</span>
          )}
        </div>
      </header>
      <article className="turns">
        {conversation.turns.map((turn, i) => (
          <Turn key={i} turn={turn} atoms={atoms} />
        ))}
      </article>
    </section>
  )
})

const Turn = memo(function Turn({ turn, atoms }) {
  const rendered = marked.parse(turn.text)
  const highlighted = highlightAtoms(rendered, atoms)
  const Icon = turn.role === 'user' ? UserIcon : AssistantIcon
  const turnClass = turn.role === 'user' ? 'user-turn' : 'assistant-turn'

  return (
    <div className={`turn ${turnClass}`}>
      <div className="turn-icon">
        <Icon />
      </div>
      <div className="turn-header">
        <div className={`turn-role ${turn.role}`}>
          {turn.role}
        </div>
        <div className="turn-rule"></div>
      </div>
      <div
        className="turn-content"
        dangerouslySetInnerHTML={{ __html: highlighted }}
      />
    </div>
  )
})

#!/usr/bin/env python3
"""
Enrich a conversation markdown file with tool calls from a Claude transcript.

This script extracts user messages, assistant messages, and tool interactions
from a JSONL transcript file and formats them according to pidgin conventions.

Format conventions:
- ## user - user text messages
- ## assistant - assistant text responses
- ## tool (ToolName) - tool invocation with parameters, blank line, then result
"""

import json
import sys

def extract_messages(jsonl_path, start_text=None):
    """Extract messages from transcript, optionally starting from a specific user message."""
    messages = []
    started = start_text is None

    with open(jsonl_path) as f:
        for line in f:
            msg = json.loads(line)
            if msg.get('type') not in ('user', 'assistant'):
                continue

            # Check for start trigger
            if not started and msg['type'] == 'user':
                content = msg['message']['content']
                if isinstance(content, str) and start_text in content:
                    started = True
                elif isinstance(content, list):
                    for item in content:
                        if item.get('type') == 'text' and start_text in item.get('text', ''):
                            started = True
                            break

            if started:
                messages.append(msg)

    return messages

def format_tool_params(tool_input):
    """Format tool parameters as key: value lines."""
    lines = []
    for key, value in tool_input.items():
        if isinstance(value, str) and '\n' in value:
            lines.append(f"{key}: |")
            for vline in value.split('\n'):
                lines.append(f"  {vline}")
        else:
            lines.append(f"{key}: {value}")
    return '\n'.join(lines)

def format_messages(messages):
    """Format messages as enriched markdown."""
    output = []
    pending_tools = {}  # tool_id -> tool_use info

    for msg in messages:
        content = msg['message']['content']

        if msg['type'] == 'user':
            if isinstance(content, str):
                # Skip command messages
                if '<command-name>' in content or '<local-command' in content:
                    continue
                output.append(f"## user\n\n{content}\n")
            elif isinstance(content, list):
                for item in content:
                    if item.get('type') == 'text':
                        text = item['text']
                        # Skip system/command stuff
                        if '<command-name>' in text or '<local-command' in text:
                            continue
                        if text.strip():
                            output.append(f"## user\n\n{text}\n")
                    elif item.get('type') == 'tool_result':
                        tool_id = item.get('tool_use_id')
                        if tool_id in pending_tools:
                            tool = pending_tools.pop(tool_id)
                            result = item.get('content', '')
                            if isinstance(result, list):
                                result = '\n'.join(r.get('text', str(r)) for r in result)

                            tool_section = f"## tool ({tool['name']})\n\n"
                            tool_section += format_tool_params(tool['input'])
                            tool_section += f"\n\n{result}\n"
                            output.append(tool_section)

        elif msg['type'] == 'assistant':
            if isinstance(content, list):
                for item in content:
                    if item.get('type') == 'text':
                        text = item['text']
                        if text.strip():
                            output.append(f"## assistant\n\n{text}\n")
                    elif item.get('type') == 'tool_use':
                        # Store for later when we get the result
                        pending_tools[item['id']] = {
                            'name': item['name'],
                            'input': item['input']
                        }
                    # Skip thinking blocks

    return '\n'.join(output)

if __name__ == '__main__':
    if len(sys.argv) < 2:
        print("Usage: enrich.py <transcript.jsonl> [start_text]")
        sys.exit(1)

    jsonl_path = sys.argv[1]
    start_text = sys.argv[2] if len(sys.argv) > 2 else None

    messages = extract_messages(jsonl_path, start_text)
    print(f"# Extracted {len(messages)} messages", file=sys.stderr)

    formatted = format_messages(messages)
    print(formatted)

