    const [, year, month, day, time] = dateMatch;
    const hours = time.slice(0, 2);
    const minutes = time.slice(2, 4);
    const seconds = time.slice(4, 6);
    date = `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;
