file.content:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: system-ui, -apple-system, sans-serif;
  background: #fafafa;
  color: #222;
  height: 100vh;
  overflow: hidden;
}

.layout {
  display: grid;
  grid-template-columns: 260px 1fr;
  height: 100vh;
}

/* Sidebar */
.sidebar {
  background: #fff;
  border-right: 1px solid #e0e0e0;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.sidebar-header {
  padding: 1.5rem 1rem 1rem;
  border-bottom: 1px solid #e0e0e0;
}

.sidebar-header h1 {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.sidebar-header .corpus-label {
  font-size: 0.75rem;
  color: #888;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.sidebar-tabs {
  display: flex;
  border-bottom: 1px solid #e0e0e0;
}

.sidebar-tab {
  flex: 1;
  padding: 0.75rem 0.5rem;
  background: none;
  border: none;
  font-size: 0.75rem;
  font-weight: 500;
  color: #666;
  cursor: pointer;
  border-bottom: 2px solid transparent;
  margin-bottom: -1px;
}

.sidebar-tab:hover {
  color: #222;
}

.sidebar-tab.active {
  color: #1976d2;
  border-bottom-color: #1976d2;
}

.sidebar-content {
  flex: 1;
  overflow-y: auto;
  padding: 0.5rem;
}

.conversation-item {
  padding: 0.75rem;
  border-radius: 6px;
  cursor: pointer;
  margin-bottom: 2px;
  transition: background 0.1s;
}

.conversation-item:hover {
  background: #f5f5f5;
}

.conversation-item.active {
  background: #e3f2fd;
}

.conversation-item .title {
  font-size: 0.9rem;
  font-weight: 500;
  margin-bottom: 0.25rem;
}

.conversation-item .meta {
  font-size: 0.75rem;
  color: #888;
}

/* Atoms */
.atom-item {
  padding: 0.6rem 0.75rem;
  border-radius: 6px;
  cursor: pointer;
  margin-bottom: 4px;
  transition: background 0.1s;
  display: flex;
  align-items: baseline;
  gap: 0.5rem;
}

.atom-item:hover {
  background: #f5f5f5;
}

.atom-item.active {
  background: #fff3e0;
}

.atom-item .term {
  font-size: 0.95rem;
  font-weight: 600;
  color: #e65100;
}

.atom-item .count {
  font-size: 0.7rem;
  color: #888;
}

.atom-item .source {
  font-size: 0.7rem;
  color: #888;
  margin-left: auto;
}

.atoms-header {
  font-size: 0.7rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.03em;
  color: #888;
  padding: 0.75rem 0.75rem 0.5rem;
}

.sidebar-footer {
  padding: 1rem;
  border-top: 1px solid #e0e0e0;
  font-size: 0.75rem;
  color: #888;
}

/* Main reader */
.reader {
  overflow-y: auto;
  padding: 2rem 3rem;
}

.corpus {
  max-width: 700px;
}

.conversation-section {
  margin-bottom: 4rem;
  padding-bottom: 2rem;
  border-bottom: 2px solid #e0e0e0;
  scroll-margin-top: 2rem;
}

.conversation-section:last-child {
  border-bottom: none;
}

.reader-inner {
  max-width: 700px;
}

.reader-header {
  margin-bottom: 2rem;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid #e0e0e0;
}

.reader-header h1 {
  font-size: 1.75rem;
  font-weight: 600;
  margin-bottom: 0.75rem;
}

.reader-meta {
  display: flex;
  gap: 1.5rem;
  font-size: 0.85rem;
  color: #666;
}

.reader-meta span {
  display: flex;
  align-items: center;
  gap: 0.35rem;
}

/* Conversation turns */
.turn {
  margin-bottom: 2.5rem;
  position: relative;
  padding-left: 2.25rem;
}

.turn-header {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin-bottom: 1rem;
}

.turn-icon {
  position: absolute;
  left: -0.25rem;
  top: -0.25rem;
  width: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
}

.turn-icon .icon {
  width: 24px;
  height: 24px;
}

.turn.user-turn .turn-icon { color: #1976d2; }
.turn.assistant-turn .turn-icon { color: #7b1fa2; }
.turn.tool-turn .turn-icon { color: #558b2f; }

.turn-role {
  font-size: 0.8rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  flex-shrink: 0;
}

.turn-role.user { color: #1976d2; }
.turn-role.assistant { color: #7b1fa2; }
.turn-role.tool { color: #558b2f; }

.turn-rule {
  flex: 1;
  height: 1px;
}

.turn.user-turn .turn-rule {
  background: linear-gradient(to right, #1976d2, transparent);
}

.turn.assistant-turn .turn-rule {
  background: linear-gradient(to right, #7b1fa2, transparent);
}

.turn.tool-turn .turn-rule {
  background: linear-gradient(to right, #558b2f, transparent);
}

.turn-content {
  font-size: 1rem;
  line-height: 1.7;
}

/* Markdown styling */
.turn-content p { margin-bottom: 1rem; }
.turn-content p:last-child { margin-bottom: 0; }

.turn-content h1, .turn-content h2, .turn-content h3 {
  margin-top: 1.5rem;
  margin-bottom: 0.75rem;
  font-weight: 600;
}

.turn-content h1 { font-size: 1.4rem; }
.turn-content h2 { font-size: 1.2rem; }
.turn-content h3 { font-size: 1.1rem; }

.turn-content strong { font-weight: 600; }
.turn-content em { font-style: italic; }

.turn-content ul, .turn-content ol {
  margin-bottom: 1rem;
  padding-left: 1.5rem;
}

.turn-content li { margin-bottom: 0.35rem; }

.turn-content code {
  font-family: 'SF Mono', Consolas, monospace;
  font-size: 0.9em;
  background: #f5f5f5;
  padding: 0.15em 0.4em;
  border-radius: 3px;
}

.turn-content pre {
  background: #2d2d2d;
  color: #f8f8f2;
  padding: 1rem;
  border-radius: 6px;
  overflow-x: auto;
