}

.turn-content blockquote {
  border-left: 3px solid #ddd;
  padding-left: 1rem;
  color: #666;
  margin-bottom: 1rem;
}

.turn-content table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 1rem;
  font-size: 0.9rem;
}

.turn-content th, .turn-content td {
  border: 1px solid #ddd;
  padding: 0.5rem 0.75rem;
  text-align: left;
}

.turn-content th {
  background: #f5f5f5;
  font-weight: 600;
}

.turn-content hr {
  border: none;
  border-top: 1px solid #e0e0e0;
  margin: 1.5rem 0;
}

/* Atom highlights in text */
.atom-highlight {
  background: linear-gradient(to bottom, transparent 60%, rgba(255, 167, 38, 0.3) 60%);
  cursor: pointer;
  transition: background 0.15s;
}

.atom-highlight:hover {
  background: rgba(255, 167, 38, 0.4);
}

/* Turn interaction */
.turn[data-turn] {
  padding: 1rem;
  margin-left: -1rem;
  margin-right: -1rem;
  border-radius: 8px;
  transition: background 0.15s;
}

.turn[data-turn].highlighted {
  background: #fff8e1;
}

/* Occurrence cards in main view */
.occurrence-card {
  background: #fff;
  border: 1px solid #e0e0e0;
  border-radius: 8px;
  padding: 1.25rem;
  margin-bottom: 1rem;
  cursor: pointer;
  transition: border-color 0.15s, box-shadow 0.15s;
}

.occurrence-card:hover {
  border-color: #bbb;
  box-shadow: 0 2px 8px rgba(0,0,0,0.05);
}

.occurrence-card .source {
  font-size: 0.75rem;
  color: #666;
  margin-bottom: 0.75rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.occurrence-card .source .role-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 28px;
  height: 28px;
  border-radius: 6px;
  flex-shrink: 0;
}

.occurrence-card .source .role-icon.user {
  color: #1976d2;
  background: #e3f2fd;
}

.occurrence-card .source .role-icon.assistant {
  color: #7b1fa2;
  background: #f3e5f5;
}

.occurrence-card .source .role-icon.tool {
  color: #558b2f;
  background: #dcedc8;
}

.occurrence-card .source .role-icon .icon {
  width: 16px;
  height: 16px;
}

.occurrence-card .source .conv-title {
  flex: 1;
}

.occurrence-card .source .scale-label {
  color: #aaa;
  font-size: 0.7rem;
}

.occurrence-card .text {
  font-size: 0.95rem;
  line-height: 1.6;
}

.occurrence-card .text p {
  margin: 0 0 0.75rem 0;
}

.occurrence-card .text p:last-child {
  margin-bottom: 0;
}

.occurrence-card .text code {
  font-size: 0.85em;
  background: #f5f5f5;
  padding: 0.15em 0.4em;
  border-radius: 3px;
}

.occurrence-card .text pre {
  background: #2d2d2d;
  color: #f8f8f2;
  padding: 0.75rem;
  border-radius: 6px;
  overflow-x: auto;
  margin: 0.5rem 0;
  font-size: 0.8rem;
}

.occurrence-card .text pre code {
  background: none;
  padding: 0;
  color: inherit;
}

.occurrence-card .text ul, .occurrence-card .text ol {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
}

.occurrence-card .text strong {
  font-weight: 600;
}

.occurrence-card .text blockquote {
  border-left: 3px solid #ddd;
  padding-left: 1rem;
  color: #666;
  margin: 0.5rem 0;
}

/* Empty state */
.empty-state {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: #888;
  font-size: 0.9rem;
}

@media (max-width: 900px) {
  .layout { grid-template-columns: 1fr; }
  .sidebar { display: none; }
  .reader { padding: 1.5rem; }
}

