  grid-template-columns: 260px 1fr;
  height: 100vh;
}

/* Sidebar */
.sidebar {
  background: #fff;
  border-right: 1px solid #e0e0e0;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.sidebar-header {
  padding: 1.5rem 1rem 1rem;
  border-bottom: 1px solid #e0e0e0;
}

.sidebar-header h1 {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.sidebar-header .corpus-label {
  font-size: 0.75rem;
  color: #888;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.sidebar-tabs {
  display: flex;
  border-bottom: 1px solid #e0e0e0;
}

.sidebar-tab {
  flex: 1;
  padding: 0.75rem 0.5rem;
  background: none;
  border: none;
  font-size: 0.75rem;
  font-weight: 500;
  color: #666;
  cursor: pointer;
  border-bottom: 2px solid transparent;
  margin-bottom: -1px;
}

.sidebar-tab:hover {
  color: #222;
}

.sidebar-tab.active {
  color: #1976d2;
  border-bottom-color: #1976d2;
}

.sidebar-content {
  flex: 1;
  overflow-y: auto;
  padding: 0.5rem;
}

.conversation-item {
  padding: 0.75rem;
  border-radius: 6px;
  cursor: pointer;
  margin-bottom: 2px;
  transition: background 0.1s;
}

.conversation-item:hover {
  background: #f5f5f5;
}

.conversation-item.active {
  background: #e3f2fd;
}

.conversation-item .title {
  font-size: 0.9rem;
  font-weight: 500;
  margin-bottom: 0.25rem;
}

.conversation-item .meta {
  font-size: 0.75rem;
  color: #888;
}

/* Atoms */
.atom-item {
  padding: 0.6rem 0.75rem;
  border-radius: 6px;
  cursor: pointer;
  margin-bottom: 4px;
  transition: background 0.1s;
  display: flex;
  align-items: baseline;
  gap: 0.5rem;
}

.atom-item:hover {
  background: #f5f5f5;
}

.atom-item.active {
  background: #fff3e0;
}

.atom-item .term {
  font-size: 0.95rem;
  font-weight: 600;
  color: #e65100;
}

.atom-item .count {
  font-size: 0.7rem;
  color: #888;
}

.atom-item .source {
  font-size: 0.7rem;
  color: #888;
  margin-left: auto;
}

.atoms-header {
