  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.03em;
  color: #888;
  padding: 0.75rem 0.75rem 0.5rem;
}

.sidebar-footer {
  padding: 1rem;
  border-top: 1px solid #e0e0e0;
  font-size: 0.75rem;
  color: #888;
}

/* Main reader */
.reader {
  overflow-y: auto;
  overflow-x: hidden;
  padding: 2rem 3rem;
}

.corpus {
  max-width: 700px;
}

.conversation-section {
  margin-bottom: 4rem;
  padding-bottom: 2rem;
  border-bottom: 2px solid #e0e0e0;
  scroll-margin-top: 2rem;
}

.conversation-section:last-child {
  border-bottom: none;
}

.reader-inner {
  max-width: 700px;
}

.reader-header {
  margin-bottom: 2rem;
  padding-bottom: 1.5rem;
  border-bottom: 1px solid #e0e0e0;
}

.reader-header h1 {
  font-size: 1.75rem;
